const scrollableDivs=document.querySelectorAll('.scrollable');scrollableDivs.forEach(div=>{let isDragging=!1;let startX,scrollLeft;div.addEventListener('mousedown',function(e){isDragging=!0;startX=e.pageX-div.offsetLeft;scrollLeft=div.scrollLeft});div.addEventListener('mouseleave',function(){isDragging=!1});div.addEventListener('mouseup',function(){isDragging=!1});div.addEventListener('mousemove',function(e){if(!isDragging)return;e.preventDefault();const x=e.pageX-div.offsetLeft;const walk=x-startX;div.scrollLeft=scrollLeft-walk})})